import React, {useContext, useEffect, useState} from 'react';
import {
  IonButton,
  IonContent,
  IonFab,
  IonFabButton,
  IonHeader,
  IonIcon, IonItem, IonLabel, IonList,
  IonModal,
  IonPage, IonTabButton,
  IonTitle,
  IonToolbar
} from '@ionic/react';
import { add } from 'ionicons/icons';
import ExploreContainer from '../components/ExploreContainer';
import './Trips.css';
import Trip from "../classes/Trip";
import firebase from "firebase";

export let trips_list: Trip[] = [];

const [trips, setTrips] = useState();

const Trips: React.FC = () => {
  return (
    <IonPage>
      <IonHeader>
        <IonToolbar>
          <IonTitle>Your Trips</IonTitle>
        </IonToolbar>
      </IonHeader>
      <IonContent>

        <IonList>
          {displayTrips()}
        </IonList>
        {/*<IonHeader collapse="condense">*/}
        {/*  <IonToolbar>*/}
        {/*    <IonTitle size="large">Your Trips</IonTitle>*/}
        {/*  </IonToolbar>*/}
        {/*</IonHeader>*/}
        {/*<ExploreContainer name="Your Trips Page" />*/}

        {/*Add Trip Button on bottom right*/}
        <IonFab vertical="bottom" horizontal="end" slot="fixed">
          <IonFabButton href="/addtrip">
            <IonIcon icon={add} />
          </IonFabButton>
        </IonFab>

      </IonContent>
    </IonPage>
  );
};

function displayTrips() {
  let tripsRef = firebase.database().ref('/trips');


  let toReturn: any[] = [];

  tripsRef.on('value', snapshot => {
    let data = snapshot.val();
    var trips = Object.values(data);
    for (let t of trips) {
      toReturn.push(<div>
          <IonItem href="">
            <IonLabel>
              <h2>{t}</h2>
            </IonLabel>
          </IonItem>
        </div>
      )
    }
  });
  return toReturn;
}


export default Trips;
