import {IonBackButton, IonButtons, IonContent, IonHeader, IonPage, IonTitle, IonToolbar} from "@ionic/react";
import React from "react";
import {RouteComponentProps} from "react-router";
import Trip from "../classes/Trip";
import {Trips, TripsContextConsumer} from "../TripsState";
import firebase from "firebase";

interface TripDetailsProps extends RouteComponentProps<{
  id: string;
}> {}


const TripDetails: React.FC<TripDetailsProps> = ({match}) => {
  let currentTripName: string = match.params.id;
  let currentTrip: Trip = findTrip(currentTripName);
  return (
    <IonPage>
      <IonHeader>
        <IonToolbar>
          <IonButtons slot="start">
            <IonBackButton defaultHref="/trips"/>
          </IonButtons>
          <TripsContextConsumer>
            {
              (context: Trips) =>
                <IonTitle>
                  {
                    (context.trips.length) ? (context.trips.map((t : Trip) => t.tripName === currentTripName ? currentTrip = t : console.log("false 2")))
                      : console.log("false")
                  }
                  {currentTrip.tripName}
                </IonTitle>
            }
          </TripsContextConsumer>
        </IonToolbar>
      </IonHeader>
      <IonContent>

      </IonContent>
    </IonPage>
  );
};

export default TripDetails;

function findTrip(tripName: string):Trip {
  let tripsRef = firebase.database().ref('/trips');
  tripsRef.on('value', snapshot => {
    let data = snapshot.val();
    if (data != null) {
      setTrips(Object.values(data));
    }
  });
}
