import React, {useState} from "react";
import {
  IonHeader,
  IonToolbar,
  IonTitle,
  IonContent,
  IonList,
  IonItem,
  IonLabel,
  IonText,
  IonInput,
  IonRadioGroup, IonListHeader, IonAvatar, IonRadio, IonButton
} from "@ionic/react";
import {Photo} from "../usePhotoGallery";
import { useForm} from "react-hook-form";


const MyModal: React.FC<{photo:Photo}> = ({photo}) => {
  let currentPhoto = photo;

  const { control, register, handleSubmit, errors, formState } = useForm();
  const [selected, setSelected] = useState<string>('custom-name');


  const onSubmit = (data: any) => {
    console.log(data.optionSelected);
    if (data.optionSelected === "custom-text") {
      console.log("custom text yoza");
    }
  };

  return (
      <div>
        <IonToolbar color="primary">
          <IonTitle>Name Item</IonTitle>
        </IonToolbar>
        <form onSubmit={handleSubmit(onSubmit)}>
          <IonList lines="full" class="ion-no-margin ion-no-padding">

            <IonItem>
              <IonLabel position="floating">
                Custom Item Name
                <IonText color="danger">*</IonText>
              </IonLabel>
              <IonInput name="itemName" type="text" ref={register({ required: true })} disabled={selected !== "custom-text"}>
              </IonInput>
            </IonItem>

            <IonRadioGroup value={selected} name="optionSelected" onIonChange={e => setSelected(e.detail.value)}>
              <IonListHeader>
                <IonLabel>Computer Vision Suggestions</IonLabel>
              </IonListHeader>

              <IonItem>
                <IonRadio slot="start" value="custom-text" />
                Custom Text
              </IonItem>

              <IonItem>
                <IonRadio slot="start" value="green" />
              </IonItem>

              <IonItem>
                <IonRadio slot="start" value="red" />
              </IonItem>

              <IonItem>
                <IonRadio slot="start" value="yellow" />
              </IonItem>
            </IonRadioGroup>

          </IonList>

          <IonButton expand="block" type="submit" class="ion-no-margin">
            Rename Item
          </IonButton>
        </form>
      </div>
  )
};

export default MyModal;
