import {
  IonBackButton,
  IonButtons,
  IonCard, IonCardContent,
  IonCardHeader, IonCardSubtitle, IonCardTitle,
  IonContent,
  IonHeader,
  IonPage,
  IonTitle,
  IonToolbar
} from "@ionic/react";
import React, {useEffect, useState} from "react";
import {RouteComponentProps} from "react-router";
import Trip from "../classes/Trip";
import {Trips, TripsContextConsumer} from "../TripsState";
import firebase from "firebase";

interface TripDetailsProps extends RouteComponentProps<{
  id: string;
}> {}


const TripDetails: React.FC<TripDetailsProps> = ({match}) => {

  const [trips, setTrips] = useState([]);

  let tripsRef = firebase.database().ref('/trips');

  useEffect(() => {
    tripsRef.on('value', snapshot => {
      let data = snapshot.val();
      if (data != null) {
        setTrips(Object.values(data));
      }
    });

  }, [] );

  let currentTripName: string = match.params.id;
  let currentTrip: Trip = new Trip("Loading...", new Date(1970, 1, 1), new Date(1970, 1, 1));

  trips.forEach((t: Trip) => {
    if (t.tripName === currentTripName) {
      currentTrip = t;
    }
  });

  return (
    <IonPage>
      <IonHeader>
        <IonToolbar>
          <IonButtons slot="start">
            <IonBackButton defaultHref="/trips"/>
          </IonButtons>
          <IonTitle>
            {currentTrip.tripName}
          </IonTitle>
        </IonToolbar>
      </IonHeader>
      <IonContent>
        <IonCard>
          <IonCardHeader>
            <IonCardTitle>{currentTrip.tripName} is the trip</IonCardTitle>
          </IonCardHeader>
          <IonCardContent>
            Lorem Ipsum
          </IonCardContent>
        </IonCard>
      </IonContent>
    </IonPage>
  );
};

export default TripDetails;
