import React, {useState} from "react";
import {
  IonHeader,
  IonToolbar,
  IonTitle,
  IonContent,
  IonList,
  IonItem,
  IonLabel,
  IonText,
  IonInput,
  IonRadioGroup, IonListHeader, IonAvatar, IonRadio, IonButton
} from "@ionic/react";
import {Photo} from "../usePhotoGallery";
import { useForm} from "react-hook-form";
import {computerVision} from "../analyseImage";


const MyModal: ({photo}: { photo: any }) => Promise<any> = async ({photo}) => {
  let currentPhoto = photo;

  const {control, register, handleSubmit, errors, formState} = useForm();
  const [selected, setSelected] = useState<string>('custom-text');

  let visionTags = await computerVision(photo);

  console.log(visionTags);

  const onSubmit = (data: any) => {
    console.log(data);
    if (data.optionSelected === "custom-text") {
      currentPhoto.description = data.itemName;
      console.log(currentPhoto);
    } else {
      currentPhoto.description = data.optionSelected;
    }
  };

  return (
    <div>
      <IonToolbar color="primary">
        <IonTitle>Name Item</IonTitle>
      </IonToolbar>
      <form onSubmit={handleSubmit(onSubmit)}>
        <IonList lines="full" class="ion-no-margin ion-no-padding">

          <IonItem>
            <IonLabel position="floating">
              Custom Item Name
              <IonText color="danger">*</IonText>
            </IonLabel>
            <IonInput name="itemName" required type="text" disabled={selected !== "custom-text"}
                      ref={register({required: false})} value={photo.description}>
            </IonInput>
          </IonItem>

          <IonRadioGroup value={selected} name="optionSelected" onIonChange={e => setSelected(e.detail.value)}
                         ref={register({required: true})}>
            <IonListHeader>
              <IonLabel>Computer Vision Suggestions</IonLabel>
            </IonListHeader>

            <IonItem>
              <IonRadio slot="start" value="custom-text"/>
              Custom Text
            </IonItem>
            {(visionTags.length) ? visionTags.map((s: string) =>
              <IonItem>
                <IonRadio slot="start" value={s}/>
                {s}
              </IonItem>
            ) : (<div></div>)
            }
          </IonRadioGroup>

        </IonList>

        <IonButton expand="block" type="submit" class="ion-no-margin">
          Rename Item
        </IonButton>
      </form>
    </div>
  )
};

export default MyModal;
