import { useForm, Controller } from "react-hook-form";
import React, {useState} from 'react';
import {IonList, IonItem, IonLabel, IonText, IonInput, IonDatetime, IonButton} from '@ionic/react';

const AddTripForm: React.FC = () => {
  const { control, register, handleSubmit, errors, formState } = useForm();

  const [data, setData] = useState();

  const onSubmit = (data: any) => {
    alert(JSON.stringify(data, null, 2));
    setData(data);
  };

  return (
      <div>
        <form onSubmit={handleSubmit(onSubmit)}>
          <IonList lines="full" class="ion-no-margin ion-no-padding">

            <IonItem>
              <IonLabel position="floating">
                Trip Name
                <IonText color="danger">*</IonText>
              </IonLabel>
              <IonInput required type="text" ref={register({ required: true })}>
              </IonInput>
            </IonItem>

            <IonItem>
              <IonLabel position="stacked">
                Start Date
              </IonLabel>
              <IonDatetime value="2020-07-31" placeholder="Select Date" ref={register({ required: true })}>
              </IonDatetime>
            </IonItem>

            <IonItem>
              <IonLabel position="stacked">
                End Date
              </IonLabel>
              <IonDatetime value="2020-07-31" placeholder="Select Date" ref={register({ required: true })}>
              </IonDatetime>
            </IonItem>
          </IonList>

          <IonButton expand="block" type="submit" class="ion-no-margin">
            Add Trip
          </IonButton>
        </form>
      </div>
  );
};

function handleFirstNameValue({event}: { event: any }) {
  let firstName = event.target.value;
  console.log(firstName);
}


export default AddTripForm;
