import React, {useState} from "react";
import {
  IonHeader,
  IonToolbar,
  IonTitle,
  IonContent,
  IonList,
  IonItem,
  IonLabel,
  IonText,
  IonInput,
  IonRadioGroup, IonListHeader, IonAvatar, IonRadio, IonButton
} from "@ionic/react";
import {Photo} from "../usePhotoGallery";
import { useForm} from "react-hook-form";


const MyModal: React.FC<{photo:Photo}> = ({photo}) => {
  let currentPhoto = photo;

  const { control, register, handleSubmit, errors, formState } = useForm();
  const [customText, setCustomText] = useState(false);


  const onSubmit = (data: any) => {
    console.log('yo');
  };

  return (
      <div>
        <form onSubmit={handleSubmit(onSubmit)}>
          <IonList lines="full" class="ion-no-margin ion-no-padding">

            <IonItem>
              <IonLabel position="floating">
                Item Name
                <IonText color="danger">*</IonText>
              </IonLabel>
              <IonInput name="itemName" required type="text" ref={register({ required: true })}>
              </IonInput>
            </IonItem>

            <IonRadioGroup value="custom-text" name="suitcaseColour" ref={register({ required: true })}>
              <IonListHeader>
                <IonLabel>Computer Vision Suggestions</IonLabel>
              </IonListHeader>

              <IonItem>
                <IonRadio slot="start" value="custom-text" />
                Custom Text
              </IonItem>

              <IonItem>
                <IonRadio slot="start" value="green" />
              </IonItem>

              <IonItem>
                <IonRadio slot="start" value="red" />
              </IonItem>

              <IonItem>
                <IonRadio slot="start" value="yellow" />
              </IonItem>
            </IonRadioGroup>

          </IonList>

          <IonButton expand="block" type="submit" class="ion-no-margin">
            Rename Item
          </IonButton>
        </form>
      {/*<IonHeader>*/}
      {/*  <IonToolbar color="primary">*/}
      {/*    <IonTitle>{currentPhoto.description}</IonTitle>*/}
      {/*  </IonToolbar>*/}
      {/*</IonHeader>*/}
      {/*<IonContent>*/}
      {/*  <p>This is the modal content.</p>*/}
      {/*</IonContent>*/}
      </div>
  )
};

export default MyModal;
